from enum import Enum


class UrgencyLevel(str, Enum):
    """Urgency level enumeration."""
    LOW = "low"
    MEDIUM = "medium"
    HIGH = "high"
    CRITICAL = "critical"


URGENCY_THRESHOLDS: dict[UrgencyLevel, float] = {
    UrgencyLevel.CRITICAL: 0.15,
    UrgencyLevel.HIGH: 0.40,
    UrgencyLevel.MEDIUM: 0.70,
    UrgencyLevel.LOW: 1.0,
}


def calculate_urgency_level(remaining_seconds: int, initial_seconds: int) -> UrgencyLevel:
    """Calculate urgency level based on remaining time as percentage of initial duration."""
    if initial_seconds == 0:
        return UrgencyLevel.LOW

    remaining_ratio = remaining_seconds / initial_seconds

    for level, threshold in sorted(URGENCY_THRESHOLDS.items(), key=lambda x: x[1]):
        if remaining_ratio <= threshold:
            return level

    return UrgencyLevel.LOW


def interpolate_urgency_colour(urgency: UrgencyLevel) -> str:
    """Return RGB hex colour for urgency level."""
    colour_map = {
        UrgencyLevel.LOW: "#00AA00",
        UrgencyLevel.MEDIUM: "#FFFF00",
        UrgencyLevel.HIGH: "#FF8800",
        UrgencyLevel.CRITICAL: "#FF0000",
    }
    return colour_map.get(urgency, "#00AA00")

